import React from "react";
// import { Box, Container, Grid } from "@mui/material";
// import { Link } from "react-router-dom";
import { MachineGrid } from "./templates/MachineGrid";
// import moment from "moment";
import { Title } from "../title.js";
import { useMachineContext } from "../../contexts/machinecontext";

// import * as React from "react";
import CssBaseline from "@mui/material/CssBaseline";
import Box from "@mui/material/Box";

import Toolbar from "@mui/material/Toolbar";
import List from "@mui/material/List";
import Typography from "@mui/material/Typography";
import Divider from "@mui/material/Divider";
import IconButton from "@mui/material/IconButton";

import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
// import Paper from "@mui/material/Paper";
import Link from "@mui/material/Link";

import ChevronLeftIcon from "@mui/icons-material/ChevronLeft";

import { mainListItems, secondaryListItems } from "./listItems";
import { Navbar } from "./templates/navbar";
import { Sidebar } from "./templates/sidebar";
import MuiAppBar from "@mui/material/AppBar";
import Badge from "@mui/material/Badge";
import MenuIcon from "@mui/icons-material/Menu";
import NotificationsIcon from "@mui/icons-material/Notifications";




export const MachineList = () => {
  const [open, setOpen] = React.useState(true);
  const toggleDrawer = () => {
    setOpen(!open);
  };

  const { machineData } = useMachineContext();
  if (machineData) {
    return (
      <Box component="main" sx={{ display: "flex" }}>
        <CssBaseline />
        <Navbar position="absolute" open={open}>
          <Toolbar
            sx={{
              pr: "24px", // keep right padding when drawer closed
            }}
          >
            <IconButton
              edge="start"
              color="inherit"
              aria-label="open drawer"
              onClick={toggleDrawer}
              sx={{
                marginRight: "36px",
                ...(open && { display: "none" }),
              }}
            >
              <MenuIcon />
            </IconButton>
            <Typography
              component="h1"
              variant="h6"
              color="inherit"
              noWrap
              sx={{ flexGrow: 1 }}
            >
              Dashboard
            </Typography>
            <IconButton color="inherit">
              <Badge badgeContent={4} color="secondary">
                <NotificationsIcon />
              </Badge>
            </IconButton>
          </Toolbar>
        </Navbar>
        <Sidebar variant="permanent" open={open}>
          <Toolbar
            sx={{
              display: "flex",
              alignItems: "center",
              justifyContent: "flex-end",
              px: [1],
            }}
          >
            <IconButton onClick={toggleDrawer}>
              <ChevronLeftIcon />
            </IconButton>
          </Toolbar>
          <Divider />
          <List component="nav">
            {mainListItems}
            <Divider sx={{ my: 1 }} />
            {secondaryListItems}
          </List>
        </Sidebar>

        <Container maxWidth={false}>
          <Grid
            container
            spacing={3}
            sx={{ alignItems: "center", justifyContents: "center" }}
          >
            <React.Fragment>
              <Title title="Molding Section Statistics" />

              {machineData.map((machine, index) => {
                // if (machine.prodRate!= 0)
                return (
                  <Grid
                    item
                    // xs={12}
                    sm={2}
                    md={2}
                    lg={2}
                    xl={1}
                    key={index}
                    sx={{
                      alignItems: "center",
                      justifyContents: "center",
                      maxWidth: 100,
                      marginBottom: 2,
                      margin: 0,
                      padding: 0,
                    }}
                  >
                    <Link
                      to={{ pathname: `machines/:${machine.machineID}` }}
                      state={{ ...machine }}
                      style={{ textDecoration: "none" }}
                    >
                      {/* <MachineGrid
                        machineId={machine.machineID}
                        monaNumber={machine.monaNumber}
                        moldId={machine.moldID}
                        status={machine.status}
                        moldShots={machine.moldShots}
                        failedShots={machine.failedShots}
                        prodRate={machine.prodRate}
                        material={machine.material}
                        moldMaker={machine.moldMaker}
                        prod_startDate={moment(machine.prod_start_date).format(
                          "DD-MM-YYYY"
                        )}
                        prod_startTime={moment(machine.prod_start_date).format(
                          "kk:mm:ss"
                        )}
                        prod_endDate={moment(machine.prod_end_date).format(
                          "DD-MM-YYYY"
                        )}
                        prod_endTime={moment(machine.prod_end_date).format(
                          "kk:mm:ss"
                        )} */}
                      <MachineGrid ID={machine.machineID} />
                    </Link>
                  </Grid>
                );
              })}
            </React.Fragment>
          </Grid>
        </Container>
      </Box>
    );
  } else {
    return <div>connection not found</div>;
  }
};
