import React, { useEffect } from "react";
// import React from "react";
// import { Box, Container, Grid } from "@mui/material";
// import { Link } from "react-router-dom";
import TimeGrid from "./utils/timegrid";
// import moment from "moment";
import { Title } from "./utils/title";
//import { useMachineContext } from "../../contexts/machinecontext";

import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
// import Paper from "@mui/material/Paper";

export const MachineList = () => {
  // const { machineData } = useMachineContext();

  const refreshPage = () => {
    window.location.reload(); // Reloads the entire page
  };

  // Effect to set up the interval for refreshing
  useEffect(() => {
    const intervalId = setInterval(refreshPage, 5000);

    // Cleanup function to clear the interval when the component is unmounted
    return () => clearInterval(intervalId);
  }, []); // Empty dependency array to run the effect only once when the component mounts

  const moldids = ["asd", "adsf"];
  if (moldids) {
    return (
      <Container maxWidth={false}>
        <Title title="Molding Section Statistics" />

        <Grid
          container
          spacing={4}
          // columnSpacing={{ xs: 1, sm: 5, md: 3 }}
          sx={{
            // display: "flex",
            display: "flex",
            alignItems: "center",
            justifyContents: "center",
            // backgroundColor: "rgba(0,0,255)",
          }}
        >
          {moldids.map((machine, index) => {
            // if (machine.prodRate!= 0)
            if (index != 0) {
              return (
                <Grid
                  item
                  xs={11}
                  sm={4}
                  md={2}
                  lg={2}
                  m={{ sm: 2, md: 4, lg: 0 }}
                  key={index}
                  sx={{
                    alignItems: "center", // Center vertically
                    justifyContent: "center", // Center horizontally
                    // marginBottom: 2,
                    // display: "flex",
                    // backgroundColor: "rgba(0,255,255)",
                  }}
                >
                  <ProgressGrid />
                </Grid>
              );
            } else {
              return (
                <Grid
                  item
                  xs={11}
                  sm={4}
                  md={2}
                  lg={2}
                  m={{ sm: 2, md: 4, lg: 0 }}
                  // sx={{ backgroundColor: "black" }}
                >
                  <TimeGrid />
                </Grid>
              );
            }
          })}

          {/* Add the TimeGrid component as the last tab */}
        </Grid>
      </Container>
    );
  } else {
    return <div>connection not found</div>;
  }
};
