// import React, { useEffect } from "react";
import React from "react";
// import { Box, Container, Grid } from "@mui/material";
// import { Link } from "react-router-dom";
import Progressbar from "./utils/Progressbar";
import TimeGrid from "./utils/timegrid";
// import moment from "moment";
import { Title } from "./utils/title";
//import { useMachineContext } from "../../contexts/machinecontext";

import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
// import Paper from "@mui/material/Paper";
// import Link from "@mui/material/Link";

export const ProgressList = () => {
  //const { machineData } = useMachineContext();
  // const refreshPage = () => {
  //   window.location.reload(); // Reloads the entire page
  // };

  // // Effect to set up the interval for refreshing
  // useEffect(() => {
  //   const intervalId = setInterval(refreshPage, 5000);

  //   // Cleanup function to clear the interval when the component is unmounted
  //   return () => clearInterval(intervalId);
  // }, []); // Empty dependency array to run the effect only once when the component mounts

  for (let i = 0; i < 24; i++) {
    let mold_shots = Math.floor(Math.random() * (100 - 20 + 1)) + 20;
    let failed_shots = Math.floor(Math.random() * (20 - 0 + 1)) + 0;

    // Choose a random status from the sta array
    let status = sta[Math.floor(Math.random() * sta.length)];

    // Ensure that "stuck" is selected fewer than two times
    if (status === "stuck" && stuckCount >= 2) {
      status = "working"; // or any other status you want to set when "stuck" limit is reached
    } else if (status === "stuck") {
      stuckCount++;
    }

    if (status === "notWorking" && notWorkingCount >= 3) {
      status = "working"; // or any other status you want to set when "stuck" limit is reached
    } else if (status === "notWorking") {
      notWorkingCount++;
    }

    const machine = {
      machineID: i,
      monaNumber: [mNumber[i - 1]],
      moldProtector: Protector[i - 1],
      status: status,
      moldMaterial: Material[Math.floor(Math.random() * Material.length)],
      moldMaker: Maker[i - 1],
      moldShots: mold_shots,
      failedShots: failed_shots,
    };
    machines.push(machine);
  }

  if (machines) {
    return (
      <Container maxWidth={false}>
        <Title title="Progress Statistics" />

        <Grid
          container
          spacing={4}
          // columnSpacing={{ xs: 1, sm: 5, md: 3 }}
          sx={{
            // display: "flex",
            display: "flex",
            alignItems: "center",
            justifyContents: "center",
            // backgroundColor: "rgba(0,0,255)",
          }}
        >
          {machines.map((machine, index) => {
            // if (machine.prodRate!= 0)
            if (index != 0) {
              return (
                <Grid
                  item
                  xs={11}
                  sm={4}
                  md={2}
                  lg={2}
                  m={{ sm: 2, md: 4, lg: 0 }}
                  key={index}
                  sx={{
                    alignItems: "center", // Center vertically
                    justifyContent: "center", // Center horizontally
                    // marginBottom: 2,
                    // display: "flex",
                    // backgroundColor: "rgba(0,255,255)",
                  }}
                >
                  <Link
                    to={{ pathname: `machines/:${machine.machineID}` }}
                    state={{ ...machine }}
                    style={{ textDecoration: "none" }}
                  >
                    <MachineGrid
                      ID={machine.machineID}
                      monaNumber={machine.monaNumber}
                      moldProtector={machine.moldProtector}
                      status={machine.status}
                      moldMaker={machine.moldMaker}
                      moldMaterial={machine.moldMaterial}
                      moldShots={machine.moldShots}
                      failedShots={machine.failedShots}
                    />
                  </Link>
                </Grid>
              );
            } else {
              return (
                <Grid
                  item
                  xs={11}
                  sm={4}
                  md={2}
                  lg={2}
                  m={{ sm: 2, md: 4, lg: 0 }}
                  // sx={{ backgroundColor: "black" }}
                >
                  <Progressbar />
                </Grid>
              );
            }
          })}

          {/* Add the TimeGrid component as the last tab */}
        </Grid>
      </Container>
    );
  } else {
    return <div>connection not found</div>;
  }
};
