import React, { useState, useEffect } from "react";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Link from "@mui/material/Link";
import { MachineGrid } from "./utils/machinegrid";
import TimeGrid from "./utils/timegrid";
import { Title } from "./utils/title";

export const MachineList = () => {
  const [machines, setMachines] = useState([]);

  useEffect(() => {
    const Material = ["GPPS", "MABS", "TPR", "HIPS", "ABS"];
    const Protector = ["Protector1", "Protector2", /* ... */ "Protector23"];
    const Maker = ["Maker1", "Maker2", /* ... */ "Maker23"];
    const sta = ["working", "notWorking", "stuck"];

    const updateMachineData = () => {
      setMachines((prevMachines) => {
        return prevMachines.map((machine) => {
          let mold_shots = Math.floor(Math.random() * (100 - 20 + 1)) + 20;
          let failed_shots = Math.floor(Math.random() * (20 - 0 + 1)) + 0;

          // Choose a random status from the sta array
          let status = sta[Math.floor(Math.random() * sta.length)];

          // Ensure that "stuck" is selected fewer than two times
          if (status === "stuck" && machine.status === "stuck") {
            status = "working"; // or any other status you want to set when "stuck" limit is reached
          }

          const updatedMachine = {
            ...machine,
            moldShots: mold_shots,
            failedShots: failed_shots,
            status: status,
          };

          return updatedMachine;
        });
      });
    };

    const intervalId = setInterval(updateMachineData, 10000); // Update every 10 seconds (adjust as needed)

    // Cleanup interval on component unmount
    return () => clearInterval(intervalId);
  }, []); // Empty dependency array means this effect runs once on mount

  if (machines.length > 0) {
    return (
      <Container maxWidth={false}>
        <Title title="Molding Section Statistics" />

        <Grid
          container
          spacing={4}
          sx={{
            display: "flex",
            alignItems: "center",
            justifyContents: "center",
          }}
        >
          {machines.map((machine, index) => {
            if (index !== 0) {
              return (
                <Grid
                  item
                  xs={11}
                  sm={4}
                  md={2}
                  lg={2}
                  m={{ sm: 2, md: 4, lg: 0 }}
                  key={index}
                  sx={{
                    alignItems: "center",
                    justifyContent: "center",
                  }}
                >
                  <Link
                    to={{ pathname: `machines/:${machine.machineID}` }}
                    state={{ ...machine }}
                    style={{ textDecoration: "none" }}
                  >
                    <MachineGrid
                      ID={machine.machineID}
                      monaNumber={machine.monaNumber}
                      moldProtector={machine.moldProtector}
                      status={machine.status}
                      moldMaker={machine.moldMaker}
                      moldMaterial={machine.moldMaterial}
                      moldShots={machine.moldShots}
                      failedShots={machine.failedShots}
                    />
                  </Link>
                </Grid>
              );
            } else {
              return (
                <Grid
                  item
                  xs={11}
                  sm={4}
                  md={2}
                  lg={2}
                  m={{ sm: 2, md: 4, lg: 0 }}
                >
                  <TimeGrid />
                </Grid>
              );
            }
          })}
        </Grid>
      </Container>
    );
  } else {
    return <div>Connection not found</div>;
  }
};
