import React from "react";
// import { Box, Container, Grid } from "@mui/material";
// import { Link } from "react-router-dom";
import { MachineGrid } from "./utils/machinegrid";
// import moment from "moment";
import { Title } from "./utils/title";
//import { useMachineContext } from "../../contexts/machinecontext";

import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
// import Paper from "@mui/material/Paper";
import Link from "@mui/material/Link";

export const MachineList = () => {
  //const { machineData } = useMachineContext();

  const Material = ["GPPS", "MABS", "TPR", "HIPS", "ABS"];

  const Protector = [
    "Protector1",
    "Protector2",
    "Protector3",
    "Protector4",
    "Protector5",
    "Protector6",
    "Protector7",
    "Protector8",
    "Protector9",
    "Protector10",
    "Protector11",
    "Protector12",
    "Protector13",
    "Protector14",
    "Protector15",
    "Protector16",
    "Protector17",
    "Protector18",
    "Protector19",
    "Protector20",
    "Protector21",
    "Protector22",
    "Protector23",
  ];

  const Maker = [
    "Maker1",
    "Maker2",
    "Maker3",
    "Maker4",
    "Maker5",
    "Maker6",
    "Maker7",
    "Maker8",
    "Maker9",
    "Maker10",
    "Maker11",
    "Maker12",
    "Maker13",
    "Maker14",
    "Maker15",
    "Maker16",
    "Maker17",
    "Maker18",
    "Maker19",
    "Maker20",
    "Maker21",
    "Maker22",
    "Maker23",
  ];

  const machines = [];
  const sta = ["working", "notWorking", "stuck"];
  const mNumber = [
    "M#123",
    "M#124",
    "M#125",
    "M#126",
    "M#127",
    "M#128",
    "M#129",
    "M#130",
    "M#131",
    "M#132",
    "M#133",
    "M#134",
    "M#135",
    "M#136",
    "M#137",
    "M#138",
    "M#139",
    "M#140",
    "M#141",
    "M#142",
    "M#143",
    "M#144",
    "M#145",
  ];

  for (let i = 0; i < 23; i++) {
    let mold_shots = Math.floor(Math.random() * (100 - 20 + 1)) + 20;
    let failed_shots = Math.floor(Math.random() * (20 - 0 + 1)) + 0;
    const machine = {
      machineID: i + 1,
      monaNumber: [mNumber[i], mNumber[i + 1]],
      moldProtector: Protector[i],
      status: sta[Math.floor(Math.random() * sta.length)], // Choose a random status from the sta array
      moldMaterial: Material[Math.floor(Math.random() * Material.length)], // Choose a random material from the Material array
      moldMaker: Maker[i],
      moldShots: mold_shots,
      failedShots: failed_shots,
    };
    machines.push(machine);
  }

  if (machines) {
    return (
      <Container maxWidth={false}>
        <Title title="Molding Section Statistics" />

        <Grid
          container
          spacing={4}
          // columnSpacing={{ xs: 1, sm: 5, md: 3 }}
          sx={{
            // display: "flex",
            display: "flex",
            alignItems: "center",
            justifyContents: "center",
            // backgroundColor: "rgba(0,0,255)",
          }}
        >
          {machines.map((machine, index) => {
            // if (machine.prodRate!= 0)
            return (
              <Grid
                item
                xs={11}
                sm={4}
                md={2}
                lg={2}
                m={{ sm: 2, md: 4, lg: 0 }}
                key={index}
                sx={{
                  alignItems: "center", // Center vertically
                  justifyContent: "center", // Center horizontally
                  // marginBottom: 2,
                  // display: "flex",
                  // backgroundColor: "rgba(0,255,255)",
                }}
              >
                <Link
                  to={{ pathname: `machines/:${machine.machineID}` }}
                  state={{ ...machine }}
                  style={{ textDecoration: "none" }}
                >
                  <MachineGrid
                    ID={machine.machineID}
                    monaNumber={machine.monaNumber}
                    moldProtector={machine.moldProtector}
                    status={machine.status}
                    moldMaker={machine.moldMaker}
                    moldMaterial={machine.moldMaterial}
                    moldShots={machine.moldShots}
                    failedShots={machine.failedShots}
                  />
                </Link>
              </Grid>
            );
          })}
          return (
          <Grid
            item
            xs={11}
            sm={4}
            md={2}
            lg={2}
            m={{ sm: 2, md: 4, lg: 0 }}
            key={index}
            sx={{
              alignItems: "center", // Center vertically
              justifyContent: "center", // Center horizontally
              // marginBottom: 2,
              // display: "flex",
              // backgroundColor: "rgba(0,255,255)",
            }}
          ></Grid>
          );
        </Grid>
      </Container>
    );
  } else {
    return <div>connection not found</div>;
  }
};
