import React, { useEffect, useState } from "react";
// import React from "react";
// import { Box, Container, Grid } from "@mui/material";
// import { Link } from "react-router-dom";
import { MachineGrid } from "./utils/machinegrid";
import TimeGrid from "./utils/timegrid";
// import moment from "moment";
import { Title } from "./utils/title";
//import { useMachineContext } from "../../contexts/machinecontext";

import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
// import Paper from "@mui/material/Paper";
import Link from "@mui/material/Link";

export const MachineList = () => {
  // const { machineData } = useMachineContext();

  const [machines, setMachines] = useState([]);

  useEffect(() => {
    const intervalId = setInterval(() => {
      updateMachineData();
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  const updateMachineData = () => {
    const updatedMachines = machines.map((machine, index) => {
      if (index < 23) {
        let mold_shots = Math.floor(Math.random() * (100 - 20 + 1)) + 20;
        let failed_shots = Math.floor(Math.random() * (20 - 0 + 1)) + 0;

        let status = machine.status;

        if (Math.random() < 0.1) {
          status = "stuck";
        } else if (Math.random() < 0.2) {
          status = "notWorking";
        } else {
          status = "working";
        }

        return {
          ...machine,
          moldShots: mold_shots,
          failedShots: failed_shots,
          status: status,
        };
      } else {
        return machine; // No updates for machines beyond the first 23
      }
    });

    setMachines(updatedMachines);
  };

  const Material = ["GPPS", "MABS", "TPR", "HIPS", "ABS"];

  const Protector = [
    "Protector1",
    "Protector2",
    "Protector3",
    "Protector4",
    "Protector5",
    "Protector6",
    "Protector7",
    "Protector8",
    "Protector9",
    "Protector10",
    "Protector11",
    "Protector12",
    "Protector13",
    "Protector14",
    "Protector15",
    "Protector16",
    "Protector17",
    "Protector18",
    "Protector19",
    "Protector20",
    "Protector21",
    "Protector22",
    "Protector23",
  ];

  const Maker = [
    "Maker1",
    "Maker2",
    "Maker3",
    "Maker4",
    "Maker5",
    "Maker6",
    "Maker7",
    "Maker8",
    "Maker9",
    "Maker10",
    "Maker11",
    "Maker12",
    "Maker13",
    "Maker14",
    "Maker15",
    "Maker16",
    "Maker17",
    "Maker18",
    "Maker19",
    "Maker20",
    "Maker21",
    "Maker22",
    "Maker23",
  ];

  const mNumber = [
    "M#123",
    "M#124",
    "M#125",
    "M#126",
    "M#127",
    "M#128_1",
    "M#129",
    "M#130",
    "M#131_1",
    "M#132",
    "M#133_2",
    "M#134",
    "M#135",
    "M#133_3",
    "M#137",
    "M#138",
    "M#128_2",
    "M#140",
    "M#141",
    "M#142",
    "M#143",
    "M#144",
    "M#145",
  ];

  if (machines.length > 0) {
    return (
      <Container maxWidth={false}>
        <Title title="Molding Section Statistics" />

        <Grid
          container
          spacing={4}
          sx={{
            display: "flex",
            alignItems: "center",
            justifyContents: "center",
          }}
        >
          {machines.map((machine, index) => {
            return (
              <Grid
                item
                xs={11}
                sm={4}
                md={2}
                lg={2}
                m={{ sm: 2, md: 4, lg: 0 }}
                key={index}
                sx={{
                  alignItems: "center",
                  justifyContent: "center",
                }}
              >
                {index !== 0 ? (
                  <Link
                    to={{ pathname: `machines/:${machine.machineID}` }}
                    state={{ ...machine }}
                    style={{ textDecoration: "none" }}
                  >
                    <MachineGrid
                      ID={machine.machineID}
                      monaNumber={machine.monaNumber}
                      moldProtector={machine.moldProtector}
                      status={machine.status}
                      moldMaker={machine.moldMaker}
                      moldMaterial={machine.moldMaterial}
                      moldShots={machine.moldShots}
                      failedShots={machine.failedShots}
                    />
                  </Link>
                ) : (
                  <TimeGrid key={index} />
                )}
              </Grid>
            );
          })}
        </Grid>
      </Container>
    );
  } else {
    return <div>connection not found</div>;
  }
};
