import React from "react";
// import { Box, Container, Grid } from "@mui/material";
// import { Link } from "react-router-dom";
import { MachineGrid } from "./utils/MachineGrid";
// import moment from "moment";
import { Title } from "./utils/title";
import { useMachineContext } from "../../contexts/machinecontext";

import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
// import Paper from "@mui/material/Paper";
import Link from "@mui/material/Link";

export const MachineList = () => {
  const { machineData } = useMachineContext();
  if (machineData) {
    return (
      <div>
        <Container maxWidth={false}>
          <Grid
            container
            spacing={2}
            sx={{
              // display: "flex",
              alignItems: "center",
              justifyContents: "center",
              backgroundColor: "rgba(0,0,255)",
            }}
          >
            <Title title="Molding Section Statistics" />
            {machineData.map((machine, index) => {
              // if (machine.prodRate!= 0)
              return (
                <Grid
                  item
                  xs={12}
                  key={index}
                  sx={{
                    alignItems: "center",
                    justifyContents: "center",
                    marginBottom: 2,
                    margin: 2,
                    display: "flex",
                    // margin: 0,
                    // padding: 0,
                    backgroundColor: "rgba(0,255,255)",
                  }}
                >
                  <Link
                    to={{ pathname: `machines/:${machine.machineID}` }}
                    state={{ ...machine }}
                    style={{ textDecoration: "none" }}
                  >
                    <MachineGrid ID={machine.machineID} />
                  </Link>
                </Grid>
              );
            })}
          </Grid>
        </Container>
      </div>
    );
  } else {
    return <div>connection not found</div>;
  }
};
