import React, { useState, useEffect } from "react";
import "./progressgrid11.css";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
} from "chart.js";
import { Line } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip
);

const MAX_POINTS = 4; // Maximum number of points to display

const initialData = {
  labels: Array.from({ length: 4 }, () => ""),
  datasets: [
    {
      data: [],
      borderColor: "rgb(53, 162, 235)",
      backgroundColor: "rgba(53, 162, 235, 0.5)",
      fill: true,
    },
  ],
};

export const ProgressGrid = () => {
  const [data, setData] = useState(initialData);

  useEffect(() => {
    const interval = setInterval(() => {
      // Generate a new random production value
      const randomValue = Math.floor(Math.random() * 10) + 1;

      // Update the data state with the new value
      setData((prevData) => {
        const newData = [...prevData.datasets[0].data, randomValue].slice(
          -MAX_POINTS
        );

        return {
          ...prevData,
          datasets: [
            {
              ...prevData.datasets[0],
              data: newData,
            },
          ],
        };
      });
    }, 1000); // Update every second

    return () => clearInterval(interval); // Cleanup the interval on component unmount
  }, []); // Empty dependency array to run the effect only once

  const [filled, setFilled] = useState(0);
  const [isRunning] = useState(true); // Start the progress bar when the component mounts

  const targetValue = Math.floor(Math.random() * (90 - 50 + 1)) + 30; // Generates a random number between 50 and 80

  useEffect(() => {
    if (filled < targetValue && isRunning) {
      setTimeout(() => setFilled((prev) => prev + 2), 50);
    }
  }, [filled, isRunning]);

  const progressPercent = filled + "%";

  return (
    <div className="working_machine1">
      <div className="tab_full_background">
        <div className="header-box">
          <span>MoldID</span>
          <img src="./headerfill-vector.svg" alt="Header SVG" />
        </div>
        <div className="tab_bodyprogress">
          <div className="graph_margin">
            <Line options={options} data={data} />
          </div>
          <div className="progressbar_margin">
            <div className="progressbar">
              <div
                style={{
                  height: "100%",
                  width: progressPercent,
                  backgroundColor: "#3E8BFF",
                  transition: "width 0.5s",
                }}
              ></div>
              <span className="progressPercent">{progressPercent}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
