import React, { useState, useEffect } from "react";
// import React from "react";
// import { Box, Container, Grid } from "@mui/material";
// import { Link } from "react-router-dom";
import { MachineGrid } from "./utils/machinegrid";
import TimeGrid from "./utils/timegrid";
// import moment from "moment";
import { Title } from "./utils/title";
//import { useMachineContext } from "../../contexts/machinecontext";

import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
// import Paper from "@mui/material/Paper";
import Link from "@mui/material/Link";

export const MachineList = () => {
  // const { machineData } = useMachineContext();

  // const refreshPage = () => {
  //   window.location.reload(); // Reloads the entire page
  // };

  // // Effect to set up the interval for refreshing
  // useEffect(() => {
  //   const intervalId = setInterval(refreshPage, 5000);

  //   // Cleanup function to clear the interval when the component is unmounted
  //   return () => clearInterval(intervalId);
  // }, []); // Empty dependency array to run the effect only once when the component mounts

  const Material = ["GPPS", "MABS", "TPR", "HIPS", "ABS"];

  const Protector = [
    "Protector1",
    "Protector2",
    "Protector3",
    "Protector4",
    "Protector5",
    "Protector6",
    "Protector7",
    "Protector8",
    "Protector9",
    "Protector10",
    "Protector11",
    "Protector12",
    "Protector13",
    "Protector14",
    "Protector15",
    "Protector16",
    "Protector17",
    "Protector18",
    "Protector19",
    "Protector20",
    "Protector21",
    "Protector22",
    "Protector23",
  ];

  const Maker = [
    "Maker1",
    "Maker2",
    "Maker3",
    "Maker4",
    "Maker5",
    "Maker6",
    "Maker7",
    "Maker8",
    "Maker9",
    "Maker10",
    "Maker11",
    "Maker12",
    "Maker13",
    "Maker14",
    "Maker15",
    "Maker16",
    "Maker17",
    "Maker18",
    "Maker19",
    "Maker20",
    "Maker21",
    "Maker22",
    "Maker23",
  ];

  const mNumber = [
    "M#123",
    "M#124",
    "M#125",
    "M#126",
    "M#127",
    "M#128_1",
    "M#129",
    "M#130",
    "M#131_1",
    "M#132",
    "M#133_2",
    "M#134",
    "M#135",
    "M#133_3",
    "M#137",
    "M#138",
    "M#128_2",
    "M#140",
    "M#141",
    "M#142",
    "M#143",
    "M#144",
    "M#145",
  ];

  const sta = ["working", "notWorking", "stuck"];
  const [machines, setMachines] = useState([]);

  useEffect(() => {
    const generateRandomValue = (max, initialMax) =>
      Math.floor(Math.random() * (max - initialMax + 1)) + initialMax;

    const generatedMachines = [];
    let stuckCount = 0;
    let notWorkingCount = 0;

    for (let i = 0; i < 24; i++) {
      const mold_shots = generateRandomValue(100, 20);
      const failed_shots = generateRandomValue(10, 0); // Ensure failedShots is initially under 10

      // Choose a random status from the sta array
      let status = sta[Math.floor(Math.random() * sta.length)];

      // Ensure that "stuck" is selected fewer than two times
      if (status === "stuck" && stuckCount >= 2) {
        status = "working";
      } else if (status === "stuck") {
        stuckCount++;
      }

      if (status === "notWorking" && notWorkingCount >= 2) {
        status = "working";
      } else if (status === "notWorking") {
        notWorkingCount++;
      }

      const machine = {
        machineID: i,
        monaNumber: [mNumber[i - 1]],
        moldProtector: Protector[i - 1],
        status: status,
        moldMaterial: Material[Math.floor(Math.random() * Material.length)],
        moldMaker: Maker[i - 1],
        moldShots: mold_shots,
        failedShots: failed_shots,
      };

      generatedMachines.push(machine);
    }

    setMachines(generatedMachines);

    // Set up intervals for each machine
    generatedMachines.forEach((machine) => {
      machine.intervalId = setInterval(() => {
        // Check if the machine is not "stuck" or "notWorking" before updating
        if (machine.status !== "stuck" && machine.status !== "notWorking") {
          machine.moldShots += 1;

          if (machine.moldShots % 50 === 0) {
            machine.failedShots += 1;
          }
        }
      }, Math.floor(Math.random() * (2000 - 1000 + 1)) + 1000); // Random interval between 1 and 2 seconds
    });

    return () => {
      // Clear intervals when the component unmounts
      generatedMachines.forEach((machine) => {
        clearInterval(machine.intervalId);
      });
    };
  }, []); // Empty dependency array to run the effect only once when the component mounts

  useEffect(() => {
    const intervalId = setInterval(() => {
      setMachines((prevMachines) => {
        const updatedMachines = [...prevMachines];

        // Randomly select a machine to update
        const randomIndex = Math.floor(Math.random() * updatedMachines.length);

        // Update moldShots and failedShots for the selected machine
        const machine = updatedMachines[randomIndex];

        // Check if the machine is not "stuck" or "notWorking" before updating
        if (machine.status !== "stuck" && machine.status !== "notWorking") {
          const newMoldShots = machine.moldShots + 1;

          // Return a new machine object with the updated values
          updatedMachines[randomIndex] = {
            ...machine,
            moldShots: newMoldShots,
          };
        } else if (machine.status === "stuck") {
          // Increment failedShots only for "stuck" machines
          const newFailedShots = machine.failedShots + 1;

          // Return a new machine object with the updated values
          updatedMachines[randomIndex] = {
            ...machine,
            failedShots: newFailedShots,
          };
        }
        // For "notWorking" machines, no change in failedShots

        return updatedMachines;
      });
    }, 1000); // Update every 5 seconds (5000 milliseconds)

    // Clear the interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  return (
    <Container maxWidth={false}>
      <Title title="Molding Section Statistics" />

      <Grid
        container
        spacing={4}
        sx={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {machines.map((machine, index) => (
          <Grid
            item
            xs={11}
            sm={4}
            md={2}
            lg={2}
            m={{ sm: 2, md: 4, lg: 0 }}
            key={index}
            sx={{
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            {index === 0 ? (
              <TimeGrid />
            ) : (
              <Link
                to={{ pathname: `machines/:${machine.machineID}` }}
                state={{ ...machine }}
                style={{ textDecoration: "none" }}
              >
                <MachineGrid
                  ID={machine.machineID}
                  monaNumber={machine.monaNumber}
                  moldProtector={machine.moldProtector}
                  status={machine.status}
                  moldMaker={machine.moldMaker}
                  moldMaterial={machine.moldMaterial}
                  moldShots={machine.moldShots}
                  failedShots={machine.failedShots}
                />
              </Link>
            )}
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};
