import React, { useState, useEffect } from "react";
// import React from "react";
// import { Box, Container, Grid } from "@mui/material";
// import { Link } from "react-router-dom";
import { MachineGrid } from "./utils/machinegrid";
import TimeGrid from "./utils/timegrid";
// import moment from "moment";
import { Title } from "./utils/title";
//import { useMachineContext } from "../../contexts/machinecontext";

import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
// import Paper from "@mui/material/Paper";
import Link from "@mui/material/Link";

export const MachineList = () => {
  // const { machineData } = useMachineContext();

  // const refreshPage = () => {
  //   window.location.reload(); // Reloads the entire page
  // };

  // // Effect to set up the interval for refreshing
  // useEffect(() => {
  //   const intervalId = setInterval(refreshPage, 5000);

  //   // Cleanup function to clear the interval when the component is unmounted
  //   return () => clearInterval(intervalId);
  // }, []); // Empty dependency array to run the effect only once when the component mounts

  const Material = ["GPPS", "MABS", "TPR", "HIPS", "ABS"];

  const Protector = [
    "Protector1",
    "Protector2",
    "Protector3",
    "Protector4",
    "Protector5",
    "Protector6",
    "Protector7",
    "Protector8",
    "Protector9",
    "Protector10",
    "Protector11",
    "Protector12",
    "Protector13",
    "Protector14",
    "Protector15",
    "Protector16",
    "Protector17",
    "Protector18",
    "Protector19",
    "Protector20",
    "Protector21",
    "Protector22",
    "Protector23",
  ];

  const Maker = [
    "Maker1",
    "Maker2",
    "Maker3",
    "Maker4",
    "Maker5",
    "Maker6",
    "Maker7",
    "Maker8",
    "Maker9",
    "Maker10",
    "Maker11",
    "Maker12",
    "Maker13",
    "Maker14",
    "Maker15",
    "Maker16",
    "Maker17",
    "Maker18",
    "Maker19",
    "Maker20",
    "Maker21",
    "Maker22",
    "Maker23",
  ];

  const mNumber = [
    "M#123",
    "M#124",
    "M#125",
    "M#126",
    "M#127",
    "M#128_1",
    "M#129",
    "M#130",
    "M#131_1",
    "M#132",
    "M#133_2",
    "M#134",
    "M#135",
    "M#133_3",
    "M#137",
    "M#138",
    "M#128_2",
    "M#140",
    "M#141",
    "M#142",
    "M#143",
    "M#144",
    "M#145",
  ];

  const sta = ["working", "notWorking", "stuck"];
  const [machines, setMachines] = useState([]);

  // ... (previous code)

  useEffect(() => {
    const generateRandomValue = (max) => Math.floor(Math.random() * (max + 1));

    const generatedMachines = [];
    let stuckCount = 0;
    let notWorkingCount = 0;

    for (let i = 0; i < 24; i++) {
      const mold_shots = generateRandomValue(100);
      const failed_shots = generateRandomValue(10); // Ensure failedShots is initially under 10

      // Choose a random status from the sta array
      let status = sta[Math.floor(Math.random() * sta.length)];

      // Ensure that "stuck" is selected fewer than two times
      if (status === "stuck" && stuckCount >= 1) {
        status = "working";
      } else if (status === "stuck") {
        stuckCount++;
      }

      if (status === "notWorking" && notWorkingCount >= 2) {
        status = "working";
      } else if (status === "notWorking") {
        notWorkingCount++;
      }

      const intervalId = setInterval(() => {
        setMachines((prevMachines) => {
          const updatedMachines = [...prevMachines];

          // Find the index of the current machine
          const machineIndex = updatedMachines.findIndex(
            (machine) => machine.machineID === i
          );

          // Check if the machine is not "stuck" or "notWorking" before updating
          if (
            updatedMachines[machineIndex].status !== "stuck" &&
            updatedMachines[machineIndex].status !== "notWorking"
          ) {
            // Update moldShots and failedShots for the selected machine
            updatedMachines[machineIndex].moldShots =
              updatedMachines[machineIndex].moldShots + 0.5;
          } else if (updatedMachines[machineIndex].status === "stuck") {
            // Increment failedShots only for "stuck" machines
            updatedMachines[machineIndex].failedShots =
              updatedMachines[machineIndex].failedShots + 0.5;
          }

          return updatedMachines;
        });
      }, Math.floor(Math.random() * (7000 - 1000 + 1)) + 5000); // Random interval between 1 and 5 seconds

      const machine = {
        machineID: i,
        monaNumber: [mNumber[i - 1]],
        moldProtector: Protector[i - 1],
        status: status,
        moldMaterial: Material[Math.floor(Math.random() * Material.length)],
        moldMaker: Maker[i - 1],
        moldShots: mold_shots,
        failedShots: failed_shots,
        intervalId: intervalId, // Store intervalId in the machine object
      };

      // generatedMachines.push(machine);
    }

    setMachines(generatedMachines);

    return () => {
      // Clear intervals when the component unmounts
      generatedMachines.forEach((machine) => {
        clearInterval(machine.intervalId);
      });
    };
  }, []); // Empty dependency array to run the effect only once when the component mounts

  // ... (remaining code)

  useEffect(() => {
    // Change the status of additional machines to "stuck" after a certain period
    const additionalStuckIntervalId = setInterval(() => {
      setMachines((prevMachines) => {
        const updatedMachines = [...prevMachines];

        // Count the current number of stuck machines
        const currentStuckCount = updatedMachines.filter(
          (machine) => machine.status === "stuck"
        ).length;

        // If the total number of stuck machines is less than 3, change the status of additional machines to "stuck"
        if (currentStuckCount < 3) {
          // Find the index of the first machine with status other than "stuck"
          const firstNonStuckIndex = updatedMachines.findIndex(
            (machine) => machine.status !== "stuck"
          );

          // If there is a machine with status other than "stuck", update its status to "stuck"
          if (firstNonStuckIndex !== -1) {
            updatedMachines[firstNonStuckIndex] = {
              ...updatedMachines[firstNonStuckIndex],
              status: "stuck",
            };
          }
        }

        return updatedMachines;
      });
    }, 8000); // Change the status every 60 seconds (60000 milliseconds)

    // Clear the interval on component unmount
    return () => clearInterval(additionalStuckIntervalId);
  }, []);

  return (
    <Container maxWidth={false}>
      <Title title="Molding Section Statistics" />

      <Grid
        container
        spacing={4}
        sx={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {machines.map((machine, index) => (
          <Grid
            item
            xs={11}
            sm={4}
            md={2}
            lg={2}
            m={{ sm: 2, md: 4, lg: 0 }}
            key={index}
            sx={{
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            {index === 0 ? (
              <TimeGrid />
            ) : (
              <Link
                to={{ pathname: `machines/:${machine.machineID}` }}
                state={{ ...machine }}
                style={{ textDecoration: "none" }}
              >
                <MachineGrid
                  ID={machine.machineID}
                  monaNumber={machine.monaNumber}
                  moldProtector={machine.moldProtector}
                  status={machine.status}
                  moldMaker={machine.moldMaker}
                  moldMaterial={machine.moldMaterial}
                  moldShots={Math.round(machine.moldShots)}
                  failedShots={Math.round(machine.failedShots)}
                />
              </Link>
            )}
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};
